INSERT INTO `tintuc` VALUES (10, 'TIN TỨC CƯỚI', '', '												<h1 class="cap" style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; font-size: 23px; font-weight: normal; text-transform: uppercase; background-color: rgb(40, 40, 40);">ĐÁM CƯỚI VIỆT CHI 21 TỶ TIỀN HOA TRÊN BÁO TÂY</h1>                                                 ', '												<p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><font color="#ffffff" style="background-color: rgb(51, 51, 51);">Mới đây, cựu wedding planner kiêm biên tập viên của một tạp chí cưới tại Australia, Wendy El Khoury đã chia sẻ về những đám cưới xa hoa nhất thế giới. Trong các ví dụ cô chia sẻ trên trên trang&nbsp;<em style="padding: 0px; outline: none; list-style: none; font-family: Tahoma, Geneva, sans-serif;">Metro</em>, Wendy có nhắc tới một đám cưới tại Việt Nam với chi phí trang trí khổng lồ.&nbsp; Wendy tiết lộ: "Karen Tran, florist nổi tiếng đã thiết kế đám cưới cho một cặp đôi người Việt Nam mà trong tiệc, chi phí dành riêng cho hoa đã lên tới 1 triệu USD, tương đương khoảng 21 tỷ đồng".</font></p><p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><font color="#ffffff" style="background-color: rgb(51, 51, 51);">Đám cưới này diễn ra vào tháng 3/2014 tại một trong những khách sạn cao cấp nhất của Hà Nội. Tiệc được sắp đặt theo ý tưởng về bữa tiệc Gatsby lộng lẫy, xa hoa. Số lượng hoa tươi sử dụng trong đám cưới có thể lên tới hàng triệu, bởi Karen Tran từng chia sẻ trên trang cá nhân, riêng cổng hoa cho đám cưới này đã tiêu tốn 5.000 bông hồng tươi nở rực rỡ</font></p><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><img src="http://i.imgur.com/ITAlADc.jpg" width="342"></p><p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><img src="http://i.imgur.com/JWAXE5S.jpg" width="342"></p><p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><img src="http://i.imgur.com/ahnJ0tw.jpg" width="342"></p></blockquote></blockquote></blockquote><p class="Normal" style="margin-bottom: 1em; padding: 0px 0px 15px; outline: none; list-style: none; font-family: arial; line-height: 18px; text-align: justify; font-stretch: normal;"><font color="#ffffff" style="background-color: rgb(0, 0, 0);"><br></font></p><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><p></p></blockquote></blockquote>                                                 ', 1, 1);
INSERT INTO `tintuc` VALUES (6, 'KHUYẾN MÃI', '', '			<h1 class="cap" style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; font-size: 23px; font-weight: normal; text-transform: uppercase; background-color: rgb(40, 40, 40);">ÁO CƯỚI VIỆT ĐĂNG: GIẢM GIÁ SỐC - CƠN LỐC QUÀ TẶNG</h1>             ', '			<p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">Sau nhiều năm tham gia thiết kế, may và hoạt động thương mại trong ngành áo cưới, Việt Đăng đã cung cấp cho cô dâu Việt Nam những sản phẩm áo cưới thời trang chất lượng cao theo tiêu chuẩn quốc tế, mẫu mã đa dạng, tinh tế với mức giá dễ chịu dành cho người Việt Nam.</p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">Áo Cưới Việt Đăng là thương hiệu độc quyền do Cục Sở Hữu Trí Tuệ cấp Tháng 8/2008. Thương hiệu Áo Cưới Việt Đăng được khẳng định qua những lần Triễn Lãm Áo Cưới thành công trong 7 năm liên tiếp.</p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><img src="http://i.imgur.com/9p3zH4B.jpg" width="359"></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify;"><span style="background-color: rgb(51, 51, 51);"><span style="color: rgb(0, 0, 0); font-family: arial, Helvetica, Tahoma, Geneva, sans-serif; line-height: normal; text-align: start;">Nhân dịp kỷ niệm 10 năm thành lập và để đáp lại sự ủng hộ nhiệt tình của khách hàng trong suốt 10 năm qua, Áo Cưới Việt Đăng có chương trình khuyến mãi đăc biệt cho khách hàng với tên gọi&nbsp;</span><span style="color: rgb(0, 0, 0); font-family: arial, Helvetica, Tahoma, Geneva, sans-serif; line-height: normal; text-align: start; padding: 0px; outline: none; list-style: none; font-weight: 600;">"Giảm giá sốc - Cơn lốc quà tặng"</span><span style="color: rgb(0, 0, 0); font-family: arial, Helvetica, Tahoma, Geneva, sans-serif; line-height: normal; text-align: start;">.</span></span></p><p style="padding: 0px 0px 15px; outline: none; list-style: none;"><br></p><p></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><i style="padding: 0px; outline: none; list-style: none;">Hãy liên hệ để trở thành người đầu tiên nhận ngay quà "khủng” nhé:</i></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><span style="padding: 0px; outline: none; list-style: none; font-weight: 600;">Áo cưới Việt Đăng 1:</span></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">105 - 107 Bình Phú, Phường 11, Quận 6, TP.HCM<br style="padding: 0px; outline: none; list-style: none;">Tel: 083 7550 467 - 083 6019 245 | Hotline: 0913 742 846</p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><span style="padding: 0px; outline: none; list-style: none; font-weight: 600;">Áo cưới Việt Đăng 2:</span></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">63 Hồ Văn Huê, Phường 9, Quận Phú Nhuận, TP.HCM<br style="padding: 0px; outline: none; list-style: none;">Tel: 086 6742 846 | Hotline: 0913 742 846</p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><span style="padding: 0px; outline: none; list-style: none; font-weight: 600;">Áo cưới Việt Đăng 3:</span></p><p style="padding: 0px 0px 15px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">136 - 136A Đặng Văn Bi, Phường Bình Thọ, Q. Thủ Đức, TP.HCM<br style="padding: 0px; outline: none; list-style: none;">Tel: 083 7223 294 | Hotline: 0913 742 846</p>             ', 1, 15);
INSERT INTO `tintuc` VALUES (7, 'ĐỊA ĐIỂM CHỤP HÌNH', '', '												<div class="pageNav" style="padding: 12px 20px; outline: none; list-style: none; color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; background: rgb(30, 30, 30);"><h1 title="Địa điểm chụp hình" style="padding: 0px; outline: none; list-style: none; font-size: 24px; font-weight: normal; float: left; text-transform: uppercase;"><br></h1><h1 class="cap" style="padding: 0px 0px 15px; outline: none; list-style: none; font-size: 23px; font-weight: normal; text-transform: uppercase; background-color: rgb(40, 40, 40);">CHỤP HÌNH CƯỚI NGOẠI CẢNH HỒ CỐC</h1></div>                                                 ', '												<span style="color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">Là một trong những địa danh được thiên nhiên ưu ái ban tặng những khung cảnh mang vẻ đẹp vừa hoang sơ vừa mộc mạc, Biển Hồ Cốc với hình vòng cung uốn lượn nằm bên cạnh những tán lá xanh của rừng nguyên sinh từ lâu đã trở thành những địa điểm được đông đảo các cặp đôi cô dâu chú rể ưa thích.</span> <div><span style="color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);"><br></span></div><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><br><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><img src="http://i.imgur.com/HuTv98j.jpg" width="342"></blockquote><br></blockquote></blockquote><span style="color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">Vì là vùng đất mới khai thác nên Hồ Cốc vẫn còn khá hoang sơ, nhưng vẻ đẹp sẵn có của nó vẫn làm người ta mê đắm. Nước biển trong xanh, tung bọt trắng xóa khi từng con sóng đua nhau xô vài những bãi đá nằm chắn ngang, khoảnh khắc này trong biển Hồ Cốc như thiên đường mờ ảo dưới từng làn khói mỏng.</span><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><div><img src="http://i.imgur.com/ydidOfJ.jpg" width="359"></div><div><br></div><div><br></div><div><img src="http://i.imgur.com/BUqiRmb.jpg" width="359"></div><div><br></div></blockquote></blockquote></blockquote><span style="color: rgb(200, 200, 200); font-family: Tahoma, Geneva, sans-serif; line-height: 22px; text-align: justify; background-color: rgb(40, 40, 40);">Những vạt rừng nguyên sinh uốn lượn hình vòng cung bên bờ biển càng làm tăng vẻ hoang sơ nhưng kiêu kỳ của vùng đất này.</span><br><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><div><img src="http://i.imgur.com/1KfTQCN.jpg"></div><div><br></div></blockquote></blockquote></blockquote><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><blockquote style="margin: 0 0 0 40px; border: none; padding: 0px;"><div><br></div><div><br></div><div><img src="http://i.imgur.com/mtU2xO7.jpg"></div></blockquote></blockquote></blockquote>                                                ', 1, 3);
